import React, { useState } from  'react';
import { Link } from 'react';
import styled from 'styled-components';
import SidebarItems from "./SidebarItems";

const SidebarParent = styled.div`
  background: #a5cff7;
  width: 250px;
  height: 100vh;
`;

const SidebarItem = styled.div`
  padding: 16px 24px;
  transition: all 0.25s ease-in-out;
  //Change the background color if 'active' prop is received
  background: ${props => props.active ? "#b15b00" : ""};
  margin: 4px 12px;
  border-radius: 4px;

  p {
    color: white;
    font-weight: bold;
    text-decoration: none;
  }

  &:hover {
    cursor:pointer;
  }

  &:hover:not(:first-child) {
    background: #c34a36;
  }
`;

function Sidebar({defaultActive}) {
    //If no active prop is passed, use `1` instead
    const [activeIndex, setActiveIndex] = useState(defaultActive || 0);

    return (
        <>
            <SidebarParent>
                <h2> I'm on the Sidebar</h2>
                <p>Love me or else!!</p>
                {
                    SidebarItems.map((item, index) => {
                        return(
                            <Link to={item.route}>
                                <SidebarItem key={item.name} active={index === activeIndex} >
                                    <p>{item.name}</p>
                                </SidebarItem>
                            </Link>
                        );
                    })
                }
            </SidebarParent>
        </>
    );
}

export default Sidebar;
